        <div className="slider-content">
        <div className="col-lg-4">
            <h1>Lorem ipsum dolor sit amet.</h1>
            <form action="#">
              <input
                className="form-control"
                type="text"
                placeholder="Learn..."
              />
            </form>
          </div>
        </div>

        </Container>
      </SwiperSlide>


    </Swiper>
  );
};

export default MainSlider;
