              <tr>
                <th>#</th>
                <th>Image</th>
                <th>Name</th>
                <th>Price</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {cartitems?.map((cart) => (
                <tr key={cart.id}>
                  <td>{cart.id}</td>

                  <td>
                    <img className="img-fluid" src={cart.photoUrl} alt="" />
                  </td>
                  <td>{cart.name}</td>
                  <td>
                    {cart.discount ? (
                      <>
                        <del>{cart.price}</del>
                        <span>{cart.discount}</span>
                      </>
                    ) : (
                      <span>{cart.price}</span>
                    )}
                  </td>
                  <button onClick={() => dispatch(RemoveCart(cart.id))}>Remove</button>
                </tr>
