import * as React from "react";
import Accordion from "@mui/material/Accordion";
import AccordionDetails from "@mui/material/AccordionDetails";
import AccordionSummary from "@mui/material/AccordionSummary";
import Typography from "@mui/material/Typography";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";

export default function CourseLessons({ lessons }) {
  const [expanded, setExpanded] = React.useState(false);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  return (
    <div>
      {lessons?.map((ls) => (
        <Accordion key={ls.lessonId}
          expanded={expanded === `p${ls.lessonId}`}
          onChange={handleChange(`p${ls.lessonId}`)}
        >
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1bh-content"
            id="panel1bh-header"
          >
            <Typography sx={{ width: "33%", flexShrink: 0 }}>
              {ls.name}
            </Typography>
            <Typography sx={{ color: "text.secondary" }}>
              I 
            </Typography>
          </AccordionSummary>
          <AccordionDetails>
            <Typography>
              {ls.LessonssVideos?.map(video=>(
                <iframe height={100} src={`https://youtu.be/dWtjKKR7Q3s?list=PLrnPJCHvNZuB5ATsJZLKX3AW4V9XaIV9b${video.videoURL}`} frameborder="0"></iframe>

              ))}
              <h1>ELEL</h1>
            </Typography>
          </AccordionDetails>
        </Accordion>
      ))}
    </div>
  );
}
