import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

const Shop = () => {
  const cart = useSelector((state) => state.cart);

  return (
    <div className="container border">
        {cart.cartitems.map((e) => (
          <div class="col-md-6 col-lg-4 col-xl-3 ">
            <div class="course-item">
              <span>{e.quantity}</span>
              <img class="img-fluid" src={e.photoUrl} alt="course-preview" />
              <div class="cbox-4-txt">
                {/* <p class="course-tags">
    <span>Languages</span>
    <span>English</span>
  </p> */}
                <h5 class="h5-xs display-inline">{e.name}</h5>
                <div class="course-rating clearfix">
                  <i class="fas fa-star"></i>
                  <i class="fas fa-star"></i>
                  <i class="fas fa-star"></i>
                  <i class="fas fa-star"></i>
                  <i class="fas fa-star-half-alt"></i>
                </div>
                <span class="course-price">
                  <span class="old-price">
                    {e.discount > 0 ? (
                      <>
                        <del>{e.price} $</del> <span>{e.discount} $</span>
                      </>
                    ) : (
                      <span>{e.price} $</span>
                    )}
                  </span>
                </span>
              </div>
            </div>
          </div>
        ))}
    </div>
  );
};

export default Shop;
