const getimage = ()=>{
    
    const randomImage =
    images[Math.floor(Math.random() * images.length)];
    setimg(randomImage);

   if(!randomImage){
    setseconImg(imeg);
     <img src={seconImg}/>
   }


}
  return (
    <>
      <button onClick={()=>getimage()} >Click me</button>
    <img src={img} style ={{height: 200,width:200}} />
    

    </>
 
  )
}

export default Task
